import React, { useEffect, useState } from "react";
import { useSelector, useDispatch } from "react-redux";
import { getIpAddressTracker } from "../../features/ipAddressTrackerSlice/ipAddressTrackerSlice";
import { MapContainer, TileLayer, Marker, Popup } from "react-leaflet";
import { Icon } from "leaflet";

export const AddressTrackerComponent = () => {
  const [addressUrl, setAddressUrl] = useState("");
  const dispatch = useDispatch();
  const { addressTracker, ipStatusFetch } = useSelector(
    (state) => state.addressTracker
  );
  const handleSubmitSearchAddress = (e) => {
    e.preventDefault();
    dispatch(getIpAddressTracker(addressUrl));
  };
  const handleChangeSearchAddress = (e) => {
    e.preventDefault();
    setAddressUrl(e.target.value);
  };
  useEffect(() => {});

  console.log(addressUrl, "<--- res");

  console.log(addressTracker);
  console.log(ipStatusFetch);
  return (
    <div>
      <form onSubmit={handleSubmitSearchAddress}>
        <input type="text" onChange={handleChangeSearchAddress} />
      </form>
      <MapContainer center={[51.505, -0.09]} zoom={13} scrollWheelZoom={false}>
        <TileLayer
          attribution='&copy; <a href="https://www.openstreetmap.org/copyright">OpenStreetMap</a> contributors'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <Marker position={[51.505, -0.09]}>
          <Popup>
            A pretty CSS3 popup. <br /> Easily customizable.
          </Popup>
        </Marker>
      </MapContainer>
    </div>
  );
};
