import { createSlice, createAsyncThunk } from "@reduxjs/toolkit";
import { BASE_URL } from "../BASE_URL_FETCH_IP_ADDRESS/BASE_URL";
import { initialState } from "../INITIAL_STATE/INITIAL_STATE";

export const getEmailAddressTracker = createAsyncThunk(
  "address/getEmailAddress",
  async (url) => {
    return await fetch(`${BASE_URL}&email=${url}`).then((r) => r.json());
  }
);

export const emailAddressTracker = createSlice({
  name: "ipAddress",
  initialState,
  reducers: {},
  extraReducers: (builder) => {
    builder.addCase(getEmailAddressTracker.fulfilled, (state, action) => {
      state.ipStatusFetch = "success";
      state.addressTracker = action.payload;
    });
    builder.addCase(getEmailAddressTracker.pending, (state) => {
      state.ipStatusFetch = "loading";
    });
    builder.addCase(getEmailAddressTracker.rejected, (state) => {
      state.ipStatusFetch = "failed";
    });
  },
});
